package codes;

/**
 * Given a string s and a string t, check if s is subsequence of t.
 *
 * A subsequence of a string is a new string which is formed from the original string by deleting some (can be none)
 * of the characters without disturbing the relative positions of the remaining characters.
 * (ie, "ace" is a subsequence of "abcde" while "aec" is not).
 */

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class IsSequence {

    public static void main(String[] args) {

        String s = "abc";
        String t = "ahcgdb";
        //String s = "leeeeetcode";
        //String t = "";
        System.out.println(isSubsequence(s,t));
    }

    /**
     * To check if string s is a subsequence of t, start from the first character of s
     * and iterate the string t to check if that character is found.
     * If yes then move to next character for str1 and check that in t.
     * If no then check for the same character of s in t.
     * @param s
     * @param t
     * @return
     */
    private static boolean isSubsequence(String s, String t) {
        if(s.length() < 0 || s.length() > 100)
            return false;
        if(t.length() < 0 || t.length() > Math.pow(10,4))
            return false;

        if(s.length() == 0)
            return true;

        int j = 0;
        for(int i = 0; i < t.length(); i++){
            if(s.charAt(j) == t.charAt(i))
                ++j;
            if(j == s.length())
                return true;
        }
        return false;
    }
}
